import React, {useEffect, useState} from 'react';
import {Container} from "react-bootstrap";
import NavBar from "../components/NavBar";
import {fetchProducts} from "../http/productAPI";
import {observer} from "mobx-react-lite";


const Adm = () => {
    const [product, setProduct] = useState([])
    useEffect(() => {
        const getProduct = async () => {
            const res = await fetch('http://localhost:5000/api/product/load')
            const getData = await res.json()
            setProduct(getData)
            console.log(getData)
        }
        getProduct()
    }, [])
    return (
        <Container className="d-flex flex-column">
            <NavBar />
            <Container className="d-flex justify-content-between mt-3 pl-3 pr-3">
                <h2 className="align-items-center"> Мои продукты </h2>
            </Container>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>Цена</th>
                        <th>Высота</th>
                        <th>Ширина</th>
                        <th>Количество</th>
                        <th>КСР</th>
                        <th>Категория</th>
                        <th>Тип</th>
                    </tr>
                </thead>
                <tbody>
                {
                    product.map( (getProd) => (
                        <tr>
                            <td> {getProd.price}</td>
                            <td> {getProd.Height}</td>
                            <td> {getProd.Width}</td>
                            <td> {getProd.Quantity}</td>
                            <td> {getProd.KSR}</td>
                            <td> {getProd.Category.Category_Name}</td>
                            <td> {getProd.Type.Type_Name}</td>
                            <td> {<button className="btn-success">Удалить</button>}</td>
                            <td> {getProd.Type.Type_Name}</td>
                        </tr>
                    ))
                }
                </tbody>
            </table>
        </Container>
    );
};

export default Adm;
