import React, {useEffect, useState} from 'react';
import {Button, Container, Modal} from "react-bootstrap";
import NavBar from "../components/NavBar";
import {fetchProducts} from "../http/productAPI";
import {observer} from "mobx-react-lite";


function Example() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Launch static backdrop modal
            </Button>

            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    I will not close if you click outside me. Don't even try to press
                    escape key.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary">Understood</Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

const Adm = () => {
    let ID = -1
    const [product, setProduct] = useState([])
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const clickShow = () => setShow(true);

    const clickDelete = (id) => {
        setShow(false)

    }

    const passID = (id) => {
        ID = id
    }

    useEffect(() => {
        const getProduct = async () => {
            const res = await fetch('http://localhost:5000/api/product/load')
            const getData = await res.json()
            setProduct(getData)
            console.log(getData)
        }
        getProduct()
    }, [])

    return (
        <Container className="d-flex flex-column">
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    I will not close if you click outside me. Don't even try to press
                    escape key.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={clickDelete}>Understood</Button>
                </Modal.Footer>
            </Modal>
            <NavBar />
            <Container className="d-flex justify-content-between mt-3 pl-3 pr-3">
                <h2 className="align-items-center"> Мои продукты </h2>
            </Container>
            <table className="table table-bordered text-bg-dark">
                <thead>
                    <tr>
                        <th>Цена</th>
                        <th>Высота</th>
                        <th>Ширина</th>
                        <th>Количество</th>
                        <th>КСР</th>
                        <th>Категория</th>
                        <th>Тип</th>
                    </tr>
                </thead>
                <tbody>
                {
                    product.map( (getProd) => (
                        <tr>
                            <td> {getProd.price}</td>
                            <td> {getProd.Height}</td>
                            <td> {getProd.Width}</td>
                            <td> {getProd.Quantity}</td>
                            <td> {getProd.KSR}</td>
                            <td> {getProd.Category.Category_Name}</td>
                            <td> {getProd.Type.Type_Name}</td>
                            <td> {
                                <button
                                    className="btn btn-success"
                                    onClick={getProd.id}
                                >
                                    Удалить
                                </button>}
                            </td>
                            <td> {getProd.Type.Type_Name}</td>
                        </tr>
                    ))
                }
                </tbody>
            </table>
        </Container>

    );
};

export default Adm;
