import React, {useContext, useEffect, useState} from 'react';
import {Button, Container, Dropdown, Form, Modal} from "react-bootstrap";
import NavBar from "../components/NavBar";
import {Context} from "../index";
import {deleteProduct} from "../http/productAPI";
import UpdateProduct from "../components/modals/updateProduct";

let ID = -1

const Adm = () => {
    const {seller} = useContext(Context)
    const sellerID = seller._sellerId
    const [product, setProduct] = useState([]);
    const [show, setShow] = useState(false);
    const [isEditProductModalOpen, setIsEditProductModalOpen] = useState(false);

    const handleClose = () => setShow(false);
    const clickShow = () => setShow(true);
    const clickShowEdit = () => setIsEditProductModalOpen(true)


    const clickDelete = (id) => {
        console.log(ID)
        deleteProduct(id)
        setShow(false)
        window.location.reload();
    }
    const passID = (id) => {
        ID = id
        console.log(ID)
    }

    useEffect(() => {
        const getProduct = async () => {
            const res = await fetch(process.env.REACT_APP_PATH_DELETION + sellerID)
            const getData = await res.json()
            setProduct(getData)
            console.log(getData)
        }
        getProduct()
    }, [])

    return (
        <Container className="d-flex flex-column">
            <Modal
                show={show}
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Удаление продукта</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Вы действительно хотите удалить продукт?
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Закрыть
                    </Button>
                    <Button variant="primary" onClick={() => {
                        clickDelete(ID);
                    }}>
                        Ок
                    </Button>
                </Modal.Footer>
            </Modal>
            <Modal
                show={show}
                onHide={onHide}
                size="lg"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Изменить продукт
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Dropdown className="mt-2 mb-2">
                            <Dropdown.Toggle>{selectedCategoryName || 'Выберите категорию'}</Dropdown.Toggle>
                            <Dropdown.Menu>
                                {Object.entries(Categories).map(([key, value]) =>
                                    <Dropdown.Item
                                        onClick={() => setSelectedCategoryName(value)}
                                    >
                                        {value}
                                    </Dropdown.Item>
                                )}
                            </Dropdown.Menu>
                        </Dropdown>
                        <Dropdown className="mt-2 mb-2">
                            <Dropdown.Toggle>{selectedTypeName || 'Выберите тип'}</Dropdown.Toggle>
                            <Dropdown.Menu>
                                {Object.entries(Types).map(([key, value]) =>
                                    <Dropdown.Item
                                        onClick={() => setSelectedTypeName(value)}
                                    >
                                        {value}
                                    </Dropdown.Item>
                                )}
                            </Dropdown.Menu>
                        </Dropdown>
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите цену"
                            onChange={e => setPrice(Number(e.target.value))}
                        />
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите высоту продукта (М)"
                            onChange={e => setHeight(Number(e.target.value))}
                        />
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите ширину продукта (мм)"
                            onChange={e => setWidth(Number(e.target.value))}
                        />
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите количество"
                            onChange={e => setQuantity(Number(e.target.value))}
                        />
                        <Form.Control
                            className="mt-3"
                            placeholder="Введите КСР"
                            onChange={e => setKSR(e.target.value)}
                        />
                        <Form.Control
                            className="mt-3"
                            type="file"
                            onChange={selectFile}
                        />
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-danger" onClick={handleCloseEdit}>Отменить</Button>
                    <Button variant="outline-success" onClick={editProduct}>Изменить</Button>
                </Modal.Footer>
            </Modal>

            <NavBar />
            <Container className="d-flex justify-content-between mt-3 pl-3 pr-3">
                <h2 className="align-items-center"> Мои продукты </h2>
            </Container>
            <table className="table table-bordered text-bg-dark">
                <thead>
                    <tr>
                        <th>Цена</th>
                        <th>Высота</th>
                        <th>Ширина</th>
                        <th>Количество</th>
                        <th>КСР</th>
                        <th>Категория</th>
                        <th>Тип</th>
                    </tr>
                </thead>
                <tbody>
                {
                    product.map( (getProd) => (
                        <tr>
                            <td> {getProd.price}</td>
                            <td> {getProd.Height}</td>
                            <td> {getProd.Width}</td>
                            <td> {getProd.Quantity}</td>
                            <td> {getProd.KSR}</td>
                            <td> {getProd.Category.Category_Name}</td>
                            <td> {getProd.Type.Type_Name}</td>
                            <td style={{textAlign: "center"}}> {
                                <button
                                    className="btn btn-success"
                                    onClick={() => {
                                        passID(getProd.id);
                                        clickShow()
                                    }}
                                >
                                    Удалить
                                </button>}
                            </td>
                            <td style={{textAlign: "center"}}> {
                                <button
                                    className="btn btn-success"
                                    onClick={() => {
                                        clickShowEdit()
                                    }}
                                >
                                    Изменить
                                </button>}
                            </td>
                        </tr>
                    ))
                }
                </tbody>
            </table>
            <UpdateProduct
                show={isEditProductModalOpen}
                onHide={() => setIsEditProductModalOpen(false)}
            />
        </Container>
    );
};

export default Adm;
