import React, {useState} from 'react';
import {Button, Dropdown, Form, Modal} from "react-bootstrap";
import {observer} from "mobx-react-lite";

const CreateProduct = observer(({show, onHide, addProduct}) => {
    const [selectedTypeName, setSelectedTypeName] = useState('')
    const [selectedCategoryName, setSelectedCategoryName] = useState('')
    const [price, setPrice] = useState(0)
    const [, setFile] = useState(null)
    const [width, setWidth] = useState(0)
    const [height, setHeight] = useState(0)
    const [quantity, setQuantity] = useState(0)
    const [KSR, setKSR] = useState('')

    const selectFile = e => {
        setFile(e.target.files[0])
    }

    // Изменить на нормальные запросы
    const Categories =
        {
            1 : 'Обои'
        }
    const Types =
        {
            1 : 'Обои бумажные',
            2 : 'Обои виниловые',
            3 : 'Обои на тканевой основе',
            4 : 'Обои водостойкие'
        }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить продукт
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{selectedCategoryName || 'Выберите категорию'}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {Object.entries(Categories).map(([key, value]) =>
                                <Dropdown.Item
                                    onClick={() => setSelectedCategoryName(value)}
                                >
                                    {value}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{selectedTypeName || 'Выберите тип'}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {Object.entries(Types).map(([key, value]) =>
                                <Dropdown.Item
                                    onClick={() => setSelectedTypeName(value)}
                                >
                                    {value}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите цену"
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите высоту продукта (мм)"
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ширину продукта (мм)"
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите количество"
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите КСР"
                    />
                    <Form.Control
                        className="mt-3"
                        type="file"
                        onChange={selectFile}
                    />
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Отменить</Button>
                <Button variant="outline-success" onClick={addProduct}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateProduct;
