import React, {useContext} from 'react';
import {Button, Dropdown, Form, Modal} from "react-bootstrap";
import {Context} from "../../index";

const CreateProduct = ({show, onHide}) => {
    let selectedCategoryName = null
    let selectedTypeName = null
    const {product} = useContext(Context)
    const Categories = {1 : 'Обои'}
    const Types =
        {
            1 : 'Обои бумажные',
            2 : 'Обои виниловые',
            3 : 'Обои на тканевой основе',
            4 : 'Обои водостойкие'
        }
    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить продукт
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{selectedCategoryName || "Выберите тип"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item
                                onClick={() => selectedCategoryName = 'Обои'}
                            >
                                {Categories["1"]}
                            </Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Form>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>Выберите тип</Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item>{Types["1"]}</Dropdown.Item>
                            <Dropdown.Item>{Types["2"]}</Dropdown.Item>
                            <Dropdown.Item>{Types["3"]}</Dropdown.Item>
                            <Dropdown.Item>{Types["4"]}</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateProduct;
